INSERT INTO `np_sys_log` VALUES ('1054', 'admin', '新增联盟校园', 'com.xhpower.education.platform.controller.SchoolController.save()', '{\"area\":\"天心区\",\"city\":\"长沙市\",\"position\":\"1\",\"preName\":\"廖德泉\",\"preSaying\":\"公——指远大理想。大公无私，廉洁奉公。爱祖国，爱学校，爱集体，把人民的利益看得高于一切\\r\\n勇——指献身精神。英勇无畏，见义勇为。勇于实践，勇于创新，勇于坚持真理，修正错误\\r\\n勤——指勤劳品质。勤学习，勤锻炼，勤奋进取，勤劳节俭\\r\\n朴——指优良作风。朴实无华，实事求是。\",\"province\":\"湖南\",\"resourceStr\":\"\",\"schDescript\":\"湖南省长沙市第一中学（The First High School of Changsha，Hunan）是湖南省教育厅直属中学，也是湖南省示范性普通高级中学、湖南省文明单位、教育部现代教育技术实验学校。\",\"schName\":\"长沙市一中\",\"themeColor\":\"rgb(219, 52, 113)\"}', '192.168.1.153', '2017-08-31 16:24:26');
