INSERT INTO `np_sys_log` VALUES ('2812', 'admin', '保存专家', 'com.xhpower.education.platform.controller.ExpertController.save()', '{\"achievement\":\"曾任耶鲁大学化学系研究员，德萨斯州农工大学化学系副教授，康宁公司生物科技资深研究员，伯特尔研究所生物科技资深研究员，香港生物科技研究院副院长，中港美三边合作药物研发公司总经理\",\"name\":\"李兆良\",\"scificAwards\":\"<p>无</p>\",\"scificRes\":\"<p><span style=\\\";font-family:宋体;font-size:14px\\\">1979<span style=\\\"font-family:宋体\\\">年中国科学院邀请讲座教授 。访问并讲学于北京，上海，广州中科院属下植物，化学，生物化学，药物等研究所。</span><span style=\\\"font-family:Calibri\\\">1979</span><span style=\\\"font-family:宋体\\\">国际抗癌联会奖金。</span><span style=\\\"font-family:Calibri\\\">1979</span><span style=\\\"font-family:宋体\\\">年招收中国赴美国德州留学的第一批研究员。</span><span style=\\\"font-family:Calibri\\\">1989-93</span><span style=\\\"font-family:宋体\\\">年担任香港生物科技研究院副院长期间，负责该院业务，促成中美港三方合作之生物科研项目及公司管理。</span></span></p><p><span style=\\\";font-family:宋体;font-size:14px\\\"><span style=\\\"font-family:宋体\\\">科学论文</span>40 <span style=\\\"font-family:宋体\\\">篇。发明专利二项 。中药研究，开创以酵素合成天然化合物，油脂类，生物碱，蛋白质化学，分离分析化学。首先在美发表有关黄耆有效成份论文。</span></span></p><p><span style=\\\";font-family:宋体;font-size:14px\\\">2006<span style=\\\"font-family:宋体\\\">年获得在美洲出土的宣德金牌 &nbsp;，为探究其来历，毅然投入历史研究，从中国史，欧洲史，美洲史，实地调查与文献并重，涉猎冶金学，旗帜学，农业发展，陶瓷发展，地图等，发现坤舆万国全图主要部分并非利玛窦所绘，而是郑和时代中国人绘制。</span></span></p><p><br/></p>\",\"status\":0,\"teachSitu\":\"<p><span style=\\\";font-family:宋体;font-size:14px\\\"><span style=\\\"font-family:宋体\\\">化学教授（退休），前香港生物科技研究院副院长。退休后，致力东西交通史研究，明代大航海，郑和，地图学等，证实明代地理大发现与地图学始于中国明代，推翻西方地理大发现学说，翻</span>600<span style=\\\"font-family:宋体\\\">年世界史谜案。</span></span></p>\",\"topic\":\"文化艺术\",\"unit\":\"香港生物科技研究院\"}', '192.168.1.37', '2017-10-24 09:59:02');
