INSERT INTO `np_sys_log` VALUES ('2931', 'admin', '保存联盟校园', 'com.xhpower.education.platform.controller.SchoolController.save()', '{\"area\":\"00330105\",\"areaText\":\"拱墅区\",\"city\":\"003301\",\"cityText\":\"杭州市\",\"id\":139,\"preName\":\"任继长\",\"preSaying\":\"和谐 求实 创新\",\"province\":\"0033\",\"provinceText\":\"浙江省\",\"resourceStr\":\"755,756\",\"schDescript\":\"杭州文澜中学是杭州学军中学与拱墅区教育局联合建办的以国有民营性质运作的学校，校区位于杭州市拱墅区。文澜中学依托学军中学，秉承学军中学办学传统：十分重视以开拓创新的精神从事教学改革、十分重视教育教学质量的高标准、十分重视学生兴趣特长的培养与学生个性的发展、十分重视创设浓厚的校园文化氛围。学校提出以学生发展为本，“学教和谐、因人施教、发展个性、提高素质”的办学理念，“高起点、高质量、现代化、国际化、有特色、创一流”的办学指导思想，在实践中得到社会各界的广泛认同，学校已成为杭州市初级中学中的名校。全校共初一、初二、初三3个年级，每年级10个班，每班约42个人，均为平行班。在校需穿校服，利用校服边上不同颜色的横杠区分年级。\",\"schName\":\"杭州文澜中学\",\"themeColor\":\"rgb(122, 178, 204)\"}', '192.168.1.37', '2017-10-24 15:30:39');
