INSERT INTO `np_sys_log` VALUES ('2933', 'admin', '保存联盟校园', 'com.xhpower.education.platform.controller.SchoolController.save()', '{\"area\":\"00510822\",\"areaText\":\"青川县\",\"city\":\"005108\",\"cityText\":\"广元市\",\"id\":138,\"preName\":\"王海元\",\"preSaying\":\"求实，奋进\",\"province\":\"0051\",\"provinceText\":\"四川省\",\"resourceStr\":\"753,754\",\"schDescript\":\"四川省青川第一高级中学，位于风光迤逦、交通便捷的四川省青川县“智慧岛”教育园区，学校前身系四川省青川三锅中学校，始建于1959年5月，是青川县境内最早举办高中教育的学校之一。“5·12”地震后，青川县委、县政府结合灾后重建以及教育资源布局调整，按国家级示范高中标准，由浙江省全额援建。2010年9月，在原三锅中学高中部的基础上正式开办“四川省青川第一高级中学”，并迁至竹园镇新址办学，被誉为川北教育的一颗璀璨明珠。\",\"schName\":\"四川省青川第一高级中学\",\"themeColor\":\"rgb(0, 0, 255)\"}', '192.168.1.37', '2017-10-24 15:34:08');
