INSERT INTO `np_sys_log` VALUES ('3072', 'admin', '保存联盟校园', 'com.xhpower.education.platform.controller.SchoolController.save()', '{\"area\":\"00445121\",\"areaText\":\"潮安县\",\"city\":\"004451\",\"cityText\":\"潮州市\",\"id\":134,\"preName\":\"许培德\",\"preSaying\":\"勤学、笃志、诚实、创新\",\"province\":\"0044\",\"provinceText\":\"广东省\",\"resourceStr\":\"745,746\",\"schDescript\":\"龙湖镇初级中学创办于1993年，是由当时镇内五所联中合并而成，位于广东省潮州市潮安区龙湖镇市头村龙鹳路中段，占地面积16660㎡，建筑面积4652㎡，现有多媒体教室、图书室、心理辅导室、团队活动室、广播室等13间功能室，总面积达到505.15㎡，有条200米环形塑胶跑道和一个运动场；校园显眼处设置有德育墙、班级日常五项评比积分栏、共青团园地、书画橱窗、教师学习心得栏等功能板块，各种设施齐全，有良好的育人环境。学校现有教职工92人，其中具备本科学历的74人，中、高职称的35人，开设有教学班25个，在校生达到1070人。\",\"schName\":\"龙湖镇初级中学\",\"themeColor\":\"rgb(0, 0, 255)\"}', '192.168.1.37', '2017-10-28 09:09:31');
