INSERT INTO `np_sys_log` VALUES ('3075', 'admin', '保存联盟校园', 'com.xhpower.education.platform.controller.SchoolController.save()', '{\"area\":\"00140211\",\"areaText\":\"南郊区\",\"city\":\"001402\",\"cityText\":\"大同市\",\"id\":130,\"preName\":\"郭双\",\"preSaying\":\"常青中学诞生于爱，又传承着爱。在爱的滋养下，注重自信教育，为成功夯实基础；注重养成教育，使文明伴随终生；注重吃苦教育，让勤勉激励人生，把“持之以恒，共创常青辉煌”作为师生的共同追求。结合生情、学情让教科研源于教学，服务于教学；实施精、细、范管理，力争让社会满意，家长放心，学生成才。\",\"province\":\"0014\",\"provinceText\":\"山西省\",\"resourceStr\":\"735,736,695,696\",\"schDescript\":\"常青中学是一所全日制普通高级中学，由区政府和马来西亚常青集团共同出资兴建，教学楼分教学、办公、实验三区，呈“一”字形排布，浩气雄浑，凭借严格的管理和优质的教学被大同市教育局评为“高考成绩优秀单位”，更被山西省政府社会事务调查中心评为“公众满意的办学单位”。主入口既作为教学楼的出入口，又兼作一个较大的主席台。教学楼总长度为129.2米，宽度为19.1米，高度为24米，总建筑面积为11002.5平方米。为了学校的更好的发展，学校开展了特色课间操，成人礼等活动。\",\"schName\":\"大同常青中学\",\"themeColor\":\"rgb(55, 230, 145)\"}', '192.168.1.37', '2017-10-28 09:10:52');
INSERT INTO `np_sys_log` VALUES ('3076', 'admin', '保存联盟校园', 'com.xhpower.education.platform.controller.SchoolController.save()', '{\"area\":\"00210404\",\"areaText\":\"望花区\",\"city\":\"002104\",\"cityText\":\"抚顺市\",\"id\":129,\"preName\":\"王森\",\"preSaying\":\"严谨务实，争创一流\",\"province\":\"0021\",\"provinceText\":\"辽宁省\",\"resourceStr\":\"737,738,691,692\",\"schDescript\":\"抚顺市第一中学，始建于1953年，1978年被定为辽宁省首批重点中学，2003年被评为辽宁省示范性普通高中。辽宁省重点高中协作校之一。2011年[1]  被评为中国百强中学。2013年获全国五四红旗团委荣誉称号。2015年再次被评为全国百强中学。由郭沫若先生亲自题写校名。学校现有48个教学班，2688名学生。有213名教职工，其中有省特级教师3人，高级教师79人，中级教师55人，中级以上职称教师占教师总数的75%，具有研究生学历的教师占40%以上，50余名国家、省、市级骨干教师分布在各个学科。学校高考成绩卓越，出过2位辽宁省高考状元，成绩一直处于全市一流水平。\",\"schName\":\"抚顺市第一中学\",\"themeColor\":\"rgb(18, 18, 59)\"}', '192.168.1.37', '2017-10-28 09:11:13');
