INSERT INTO `np_sys_log` VALUES ('3078', 'admin', '保存联盟校园', 'com.xhpower.education.platform.controller.SchoolController.save()', '{\"area\":\"00321324\",\"areaText\":\"泗洪县\",\"city\":\"003213\",\"cityText\":\"宿迁市\",\"id\":127,\"preName\":\"黄振宇\",\"preSaying\":\"树德励才，求索鼎新\",\"province\":\"0032\",\"provinceText\":\"江苏省\",\"resourceStr\":\"741,742,683,684\",\"schDescript\":\"江苏省泗洪中学，系省四星级高中（国家级示范高中），是“江苏省名中学”。该校建于1949年，1960年受到国务院嘉奖，1980年被省政府批准为全省首批办好的95所省重点中学之一，1993年通过省重点中学合格验收，2004年晋升为四星级高中。 校园占地300亩，现在校师生近8000人。学校拥有科技馆、体育馆、图书馆、实验楼、生态园、校史馆等基础硬件设施，曲桥流水，假山喷泉，亭榭长廊，鸟语花香，被誉为书香校园和人文花园，在环保、科技教育方面形成了一定的传统优势和办学特色。\",\"schName\":\"江苏省泗洪中学\",\"themeColor\":\"rgb(0, 0, 255)\"}', '192.168.1.37', '2017-10-28 09:11:54');
