INSERT INTO `np_sys_log` VALUES ('3099', 'admin', '保存专家', 'com.xhpower.education.platform.controller.ExpertController.save()', '{\"achievement\":\"中国社会科学院新闻与传播研究所教授,博士生导师\\u00A0,媒介传播与儿童发展研究中心主任,兼任英国救助儿童会顾问\",\"email\":\"buwei@qq.com\",\"name\":\"卜卫\",\"password\":\"123456\",\"scificAwards\":\"<p><span style=\\\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-weight: normal;font-style: normal;font-size: 14px\\\"><span style=\\\"font-family:宋体\\\">曾获</span>1997<span style=\\\"font-family:宋体\\\">年获国家图书奖提名奖、中国社会科学院和共青团中央全国青年社会科学优秀成果“专著”奖、中国社会科学院优秀成果三等奖以及文化部“蒲公英奖”少儿文化论文专著金奖等。</span></span></p><p><br/></p>\",\"scificRes\":\"<p><span style=\\\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-weight: normal;font-style: normal;font-size: 14px\\\"><span style=\\\"font-family:宋体\\\">《进入地球村</span>-<span style=\\\"font-family:宋体\\\">大众传播与中国儿童》为我国第一本有关儿童与传播的研究著作，以后出版《大众媒介对儿童的影响》、《媒介与儿童教育》、《促进农村校外青少年健康教育的传播战略研究》等六本专著，在国内外发表论文及内部研究报告约</span><span style=\\\"font-family:Arial\\\">400</span><span style=\\\"font-family:宋体\\\">万字。</span></span><strong></strong></p><p><br/></p>\",\"section\":\"幼儿教育\",\"sex\":\"0\",\"status\":1,\"teachSitu\":\"<p><span style=\\\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-weight: normal;font-style: normal;font-size: 14px\\\"><span style=\\\"font-family:宋体\\\">主持课题研究，包括国家社会科学基金课题</span>“大众传播对儿童的影响”和联合国儿童基金会等国际机构的儿童研究项目，其内容包括儿童与媒介新技术、改善儿童青少年公共健康、艾滋病预防与干预、女童教育、减少针对儿童的暴力、促进儿童福利制度、反对针对儿童拐卖、发展儿童青少年草根文化以及促进媒介素养教育。</span></p><p><br/></p>\",\"telephone\":\"puwei\",\"topic\":\"心理健康\",\"unit\":\"中国社会科学院新闻与传播研究所\"}', '192.168.1.37', '2017-10-28 09:28:51');
