INSERT INTO `np_sys_log` VALUES ('4077', 'admin', '内容发布', 'com.xhpower.education.platform.controller.CmsController.save()', '{\"accessoryResources\":{\"id\":0,\"name\":\"HID]K~7C@UX]X[97WMQ[F21.png\",\"path\":\"http://baktest-10012356.file.myqcloud.com/synthDailyrecord/2017-11-09/af12be9251c6440e9c5cfc77c423c755.png\"},\"content\":\"<p>此文章有封面有附件</p>\",\"coverResources\":{\"id\":0,\"name\":\"120jiqimao.jpg\",\"path\":\"http://baktest-10012356.file.myqcloud.com/synthDailyrecord/2017-11-09/d9c954bce65644dfbe0ac98ff7364388.jpg\"},\"expert\":\"\",\"from\":\"1\",\"id\":135,\"news\":0,\"position\":0,\"subheading\":\"有封面，有附件\",\"tag\":\"文化艺术\",\"tagId\":\"\",\"theme\":\"\",\"title\":\"测试：封面图和附件\",\"type\":\"39be212a9fd14d91bd72503386059272\",\"typePath\":\"66815b1c74e8405f8136bb5af4993759,39be212a9fd14d91bd72503386059272\"}', '218.76.1.150', '2017-11-09 15:21:16');
INSERT INTO `np_sys_log` VALUES ('4078', 'admin', '内容发布', 'com.xhpower.education.platform.controller.CmsController.save()', '{\"accessoryResources\":{\"id\":0,\"name\":\"120jiqimao.jpg\",\"path\":\"http://baktest-10012356.file.myqcloud.com/synthDailyrecord/2017-11-09/5f2f54533acc42b8b640e0632e00b2aa.jpg\"},\"content\":\"<p>让孩子来决定。这本来就是孩子交际圈子的事情，他们之间你\\n来我往送了一些什么样的东西自己最清楚。对方同学家里的经济状\\n况、喜好他也大致了解，所以还是尊重孩子的意见为好。\\n可以提前跟孩子说好，比如买礼物的钱可以从自己的零花钱或\\n压岁钱里出，如果不够的话，可采用预支下次的零花钱或用干家务\\n活等方式自己挣得。这样孩子做决定的时候会很慎重。\\n当然，如果孩子向父母求助，父母可以在听完孩子的想法后给\\n出一些建议。同时，把朋友间送礼物的一些基本原则讲给孩子听：\\n比如要考虑对方的喜好；送的东西应当是自己也比较喜欢的；选礼\\n物贵在用心，态度真诚很重要，如果看重的是价格，为什么朋友间\\n送礼物不直接送钱呢？\\n至于该送什么给同学，主意还是让孩子拿吧。</p>\",\"coverResources\":{\"id\":0,\"name\":\"\",\"path\":\"\"},\"expert\":\"\",\"from\":\"教育网\",\"id\":128,\"news\":0,\"position\":0,\"subheading\":\"\",\"tag\":\"教育,文化艺术\",\"tagId\":\"\",\"theme\":\"\",\"title\":\"孩子的同学要过生日了，买什么礼物比较合适呢？\",\"type\":\"79181c257bd24b67871aef7015704b87\",\"typePath\":\"6b00f6a0427140e798f776eb4b38ca4e,79181c257bd24b67871aef7015704b87\"}', '218.76.1.150', '2017-11-09 15:23:04');
