INSERT INTO `np_sys_log` VALUES ('4093', 'admin', '保存联盟校园', 'com.xhpower.education.platform.controller.SchoolController.save()', '{\"address\":\"\",\"area\":\"00330105\",\"areaText\":\"拱墅区\",\"city\":\"003301\",\"cityText\":\"杭州市\",\"id\":139,\"infreceiSitP\":\"\",\"infreceiman\":\"\",\"infreceiphone\":\"\",\"preName\":\"任继长\",\"prePhone\":\"\",\"preSaying\":\"和谐 求实 创新\",\"preSitPho\":\"\",\"province\":\"0033\",\"provinceText\":\"浙江省\",\"resourceStr\":\"841,842\",\"schDescript\":\"杭州文澜中学是杭州学军中学与拱墅区教育局联合建办的以国有民营性质运作的学校，校区位于杭州市拱墅区。文澜中学依托学军中学，秉承学军中学办学传统：十分重视以开拓创新的精神从事教学改革、十分重视教育教学质量的高标准、十分重视学生兴趣特长的培养与学生个性的发展、十分重视创设浓厚的校园文化氛围。学校提出以学生发展为本，“学教和谐、因人施教、发展个性、提高素质”的办学理念，“高起点、高质量、现代化、国际化、有特色、创一流”的办学指导思想，在实践中得到社会各界的广泛认同，学校已成为杭州市初级中学中的名校。全校共初一、初二、初三3个年级，每年级10个班，每班约42个人，均为平行班。在校需穿校服，利用校服边上不同颜色的横杠区分年级。\",\"schName\":\"杭州文澜中学\",\"status\":0,\"themeColor\":\"rgb(0, 0, 255)\",\"unitSitPhone\":\"\",\"unitTelPhone\":\"\"}', '218.76.1.150', '2017-11-10 09:10:24');
INSERT INTO `np_sys_log` VALUES ('4094', 'admin', '保存联盟校园', 'com.xhpower.education.platform.controller.SchoolController.save()', '{\"address\":\"\",\"area\":\"00510822\",\"areaText\":\"青川县\",\"city\":\"005108\",\"cityText\":\"广元市\",\"id\":138,\"infreceiSitP\":\"\",\"infreceiman\":\"\",\"infreceiphone\":\"\",\"preName\":\"王海元\",\"prePhone\":\"\",\"preSaying\":\"求实，奋进\",\"preSitPho\":\"\",\"province\":\"0051\",\"provinceText\":\"四川省\",\"resourceStr\":\"843,844\",\"schDescript\":\"四川省青川第一高级中学，位于风光迤逦、交通便捷的四川省青川县“智慧岛”教育园区，学校前身系四川省青川三锅中学校，始建于1959年5月，是青川县境内最早举办高中教育的学校之一。“5·12”地震后，青川县委、县政府结合灾后重建以及教育资源布局调整，按国家级示范高中标准，由浙江省全额援建。2010年9月，在原三锅中学高中部的基础上正式开办“四川省青川第一高级中学”，并迁至竹园镇新址办学，被誉为川北教育的一颗璀璨明珠。\",\"schName\":\"四川省青川第一高级中学\",\"status\":0,\"themeColor\":\"rgb(0, 0, 255)\",\"unitSitPhone\":\"\",\"unitTelPhone\":\"\"}', '218.76.1.150', '2017-11-10 09:11:35');
